<script>
import { state } from '../state';

export default {
    name: "AppMain",

    data() {
        return {
            state: state,
        }
    },
    methods: {
        ritornaParola(str) {

            if (str === 'xx') {
                return `data:image/png;base64,`
            } else if (str === 'ja') {
                return `data:image/png;base64,`
            } else if (str === 'ko') {
                return `data:image/png;base64,`
            } else if (str === 'zh') {
                return `data:image/png;base64,`
            } else if (str === 'jv') {
                return `data:image/png;base64,`
            } else if (str === 'en') {
                return `data:image/png;base64,`
            } else if (str === 'ta') {
                return `data:image/png;base64,`
            } else if (str === 'cs') {
                return `data:image/png;base64,`
            } else if (str === 'te') {
                return `data:image/png;base64,`
            } else if (str === 'hi') {
                return `data:image/png;base64,`
            } else if (str === 'da') {
                return `data:image/png;base64,`
            } else if (str === 'or') {
                return `data:image/png;base64,`
            } else if (str === 'fa') {
                return `data:image/png;base64,`
            }
            else {
                return `https://flagsapi.com/${str.toUpperCase()}/flat/64.png`
            }

        },

        returnNumb(number) {

            const newNumber = number / 2;
            let intero = (Math.trunc(newNumber)); //il metodo trunc mi permette di troncare la parte decimale e prendere solo il numero intero e lo salvo in numb

            const decimale = newNumber - intero
            //console.log("numero originale: " + number + "numero diviso: " + newNumber + "parte intera:" + intero + "parte decimale:" + Number(decimale.toFixed(2)))

            if (Number(decimale.toFixed(2)) > 0.5) {
                intero++
            } else if (number <= 0) {
                intero = 1
            }

            return intero
        }
    }
}
</script>

<template>
    <main>
        <div class="container">
            <div class="row">
                <div class="col" v-for="film in state.filmsList">
                    <div class="card">

                        <div>
                            {{ "titolo: " + film.title }}
                        </div>
                        <div>
                            {{ "titolo originale:" + film.original_title }}
                        </div>
                        <div>
                            <!--trasformo la string in maiuscolo, in modo tale che dopo posso inserirla nella ricerca immagine-->
                            {{ "lingua originale: " }}

                            <img :src="ritornaParola(film.original_language)" alt="" class="flag">
                        </div>
                        <div>
                            {{ "voto :" }}
                            <span class="star_color" v-for="n in returnNumb(film.vote_average)"><i class="fa fa-star"
                                    aria-hidden="true"></i></span>
                        </div>
                        <div>
                            <img :src="`https://image.tmdb.org/t/p/w342/${film.poster_path}`" alt="">
                        </div>

                    </div>
                </div>

            </div>
        </div>
    </main>
</template>

